using BinaryProvider # requires BinaryProvider 0.3.0 or later

# Parse some basic command-line arguments
const verbose = "--verbose" in ARGS
const prefix = Prefix(get([a for a in ARGS if a != "--verbose"], 1, joinpath(@__DIR__, "usr")))
products = [
    LibraryProduct(prefix, String["libtasmaniansparsegrid"], :TASlib),
]

# Download binaries from hosted location
bin_prefix = "https://github.com/floswald/TasmanianBuilder/releases/download/v0.1"

# Listing of files generated by BinaryBuilder:
download_info = Dict(
    Linux(:aarch64, :glibc) => ("$bin_prefix/Tasmanian.aarch64-linux-gnu.tar.gz", "d97158e2ce5b94714943f9e5a2a3ff12c246567c3f74085c7e6439d22cbdec5d"),
    Linux(:aarch64, :musl) => ("$bin_prefix/Tasmanian.aarch64-linux-musl.tar.gz", "1acbd84349fc4b4a4bf7d8e6928321121e188d98044fc121442222e050116d37"),
    Linux(:armv7l, :glibc, :eabihf) => ("$bin_prefix/Tasmanian.arm-linux-gnueabihf.tar.gz", "634eb7667c914557f5526c5e4943a262c528f633d4a0b6dd4967ee8c4d3243b5"),
    Linux(:armv7l, :musl, :eabihf) => ("$bin_prefix/Tasmanian.arm-linux-musleabihf.tar.gz", "6802e1fa5670dfbb09ae8aed7d69c2101e4d96ff682a542513a833c94b0e75c6"),
    Linux(:i686, :glibc) => ("$bin_prefix/Tasmanian.i686-linux-gnu.tar.gz", "15d5ca78ed14291a65d8eb512573c9298f22d43f207b283eb254bd54bb0bff38"),
    Linux(:i686, :musl) => ("$bin_prefix/Tasmanian.i686-linux-musl.tar.gz", "400206795ba8fd1be09446ffd43587d169f1ec63a17dd566dddbb091aca9c00a"),
    Windows(:i686) => ("$bin_prefix/Tasmanian.i686-w64-mingw32.tar.gz", "7245e3b1dd4642b9e551c958122cbbcebd275b73188969642694d20c680c5897"),
    Linux(:powerpc64le, :glibc) => ("$bin_prefix/Tasmanian.powerpc64le-linux-gnu.tar.gz", "88f397d829e00448b9ea3232ff72b9e80b791eb7a31f6b17e43d160f79aeaf25"),
    MacOS(:x86_64) => ("$bin_prefix/Tasmanian.x86_64-apple-darwin14.tar.gz", "f1bb11ea34458bf842e5d13e2968a81a93b65958b6714e39eb85ec1faefc8865"),
    Linux(:x86_64, :glibc) => ("$bin_prefix/Tasmanian.x86_64-linux-gnu.tar.gz", "3c84a2cbf6ae870be5a2466b14d7007fe6cb387a14284e8a010e770dda3bcd0f"),
    Linux(:x86_64, :musl) => ("$bin_prefix/Tasmanian.x86_64-linux-musl.tar.gz", "fa399ca87d65504e5da8ee68e8b623b626f9a91fc4bc36a6141143debf9fde56"),
    Windows(:x86_64) => ("$bin_prefix/Tasmanian.x86_64-w64-mingw32.tar.gz", "6d948b371a9ebeee26db5bd26d640dd3ff155c8edf837b2de19c7e3d0d4fb3a2"),
)

# Install unsatisfied or updated dependencies:
unsatisfied = any(!satisfied(p; verbose=verbose) for p in products)
if haskey(download_info, platform_key())
    url, tarball_hash = download_info[platform_key()]
    if unsatisfied || !isinstalled(url, tarball_hash; prefix=prefix)
        # Download and install binaries
        install(url, tarball_hash; prefix=prefix, force=true, verbose=verbose)
    end
elseif unsatisfied
    # If we don't have a BinaryProvider-compatible .tar.gz to download, complain.
    # Alternatively, you could attempt to install from a separate provider,
    # build from source or something even more ambitious here.
    error("Your platform $(triplet(platform_key())) is not supported by this package!")
end

# Write out a deps.jl file that will contain mappings for our products
write_deps_file(joinpath(@__DIR__, "deps.jl"), products)
